package com.string;
/*@Chaithra K
java program to find the index of a specific word in a string  
*/
public class index {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String ck="I am Chaithra from Tumkur and completed engineering in EWIT"; // given string
		int res=ck.indexOf("completed"); // to find the index of this world
		System.out.println("the index of a given word in a string is : ");
		System.out.println(res); //printing the index of the world
		
	}

}
